import React from "react";
import { Link } from "react-router-dom";
import { Box, Heading, Flex, Button } from "@chakra-ui/react";

// Note: This code could be better, so I'd recommend you to understand how I solved and you could write yours better :)
const Header = (props) => {
  const [show, setShow] = React.useState(false);
  const handleToggle = () => setShow(!show);

  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      padding="1.5rem"
      bg="#009EDB"
      color="white"
      {...props}
    >
      <Flex align="center" mr={5}>
        <Link to="/">
          <Heading as="h1" size="lg" letterSpacing={"-.01rem"}>
            Wills Stern
          </Heading>
        </Link>
      </Flex>

      <Button
      display={{ base: "block", md: "none" }}
      onClick={handleToggle}
      variant = 'outline'>
        <svg
          fill="white"
          width="20px"
          viewBox="0 0 20 20"
          xmlns="http://www.w3.org/2000/svg"
        >
          <title>Menu</title>
          <path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z" />
        </svg>
      </Button>

      <Box
        display={{ sm: show ? "block" : "none", md: "flex" }}
        width={{ sm: "full", md: "auto" }}
        alignItems="center">

        <Link to="/contacts" _hover={{color: "black"}}>
          <Button variant="ghost">Contacts</Button>
        </Link>
        <Link to="/resume" _hover={{color: "black"}}>
          <Button variant="ghost">Resume</Button>
        </Link>
        <Link to="/photos" _hover={{color: "black"}}>
          <Button variant="ghost">Photos</Button>
        </Link>
        <Link to="/projects" _hover={{color: "black"}}>
          <Button variant="ghost">Projects</Button>
        </Link>

      </Box>
    </Flex>
  );
};

export default Header;
