import React from "react";
import { Link } from "react-router-dom";
import { Box, Heading, Flex, Button, PseudoBox } from "@chakra-ui/react";

// Note: This code could be better, so I'd recommend you to understand how I solved and you could write yours better :)
const Header = (props) => {
  const [show, setShow] = React.useState(false);
  const handleToggle = () => setShow(!show);

  const [isVisible, setIsVisible] = React.useState(false);

  function over(e) {
    setIsVisible(true);
  }
  function out(e) {
    setIsVisible(false);
  }

  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      padding="1.5rem"
      bg="#009EDB"
      color="white"
      {...props}
    >
      <Flex align="center" mr={5}>
        <Link to="/">
          <Heading as="h1" size="lg" letterSpacing={"-.01rem"}>
            Wills Stern
          </Heading>
        </Link>
      </Flex>

      <Button
      display={{ base: "block", md: "none" }}
      onClick={handleToggle}
      variant = 'outline'
      _hover={{color: "black"}}>
        <svg
          fill="white"
          width="20px"
          viewBox="0 0 20 20"
          xmlns="http://www.w3.org/2000/svg"
        >
          <title>Menu</title>
          <path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z" />
        </svg>
      </Button>



      <PseudoBox
        as="button"
        color="blue.700"
        fontWeight="semibold"
        py={2}
        px={4}
        borderWidth="1px"
        borderColor="blue.500"
        rounded="md"
        _hover={{ bg: "blue.500", color: " white" }}
        _focus={{ boxShadow: "outline" }}
        onMouseOver={over}
        onMouseOut={out}
      >
        Hover me
        <Box
        display={{ sm: show ? "block" : "none", md: "flex" }}
        width={{ sm: "full", md: "auto" }}
        alignItems="center">

        <Link to="/contacts">
          <Button variant="ghost" _hover={{color: "black"}}>Contacts</Button>
        </Link>
        <Link to="/resume">
          <Button variant="ghost" _hover={{color: "black"}}>Resume</Button>
        </Link>
        <Link to="/photos">
          <Button variant="ghost" _hover={{color: "black"}}>Photos</Button>
        </Link>
        <Link to="/projects">
          <Button variant="ghost" _hover={{color: "black"}}>Projects</Button>
        </Link>

      </Box>
      </PseudoBox>



      <Box
        display={{ sm: show ? "block" : "none", md: "flex" }}
        width={{ sm: "full", md: "auto" }}
        alignItems="center">

        <Link to="/contacts">
          <Button variant="ghost" _hover={{color: "black"}}>Contacts</Button>
        </Link>
        <Link to="/resume">
          <Button variant="ghost" _hover={{color: "black"}}>Resume</Button>
        </Link>
        <Link to="/photos">
          <Button variant="ghost" _hover={{color: "black"}}>Photos</Button>
        </Link>
        <Link to="/projects">
          <Button variant="ghost" _hover={{color: "black"}}>Projects</Button>
        </Link>

      </Box>
    </Flex>
  );
};

export default Header;
